import React from "react";
import axios from "axios";

class UserSignUpPage extends React.Component{

    state = {//Comes from React.components
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null
    };
  

    onChange = event => {
        const { name, value} = event.target
        // Shorthand 
        //const value = event.target.value;
        //const nameField = event.target.name;
        this.setState({
            [name]: value
        })
    }
    
    onClickSignUp = event =>{
        event.preventDefault();//We block browser

        const body = {
            username: this.state.username,
            displayname: this.state.displayname,
            password: this.state.password,
            passwordRepeat: this.state.passwordRepeat
        };
        
        axios.post("http://localhost:8080/api/1.0/users", body);
    }

    render(){
        return(
            <form>
                <h1>Sign Up Page</h1>
                <div>
                    <label>Username</label>
                    <input name="username" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Display Name</label>
                    <input name="displayName" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Password</label>
                    <input name="password"type="password" onChange={this.onChange}/>
                </div>
                <div>
                    <label>Password Repeat</label>
                    <input name="passwordRepeat" type="password" onChange={this.onChange}/>
                </div>
                <div>
                    <button class="signUpButton" onClick={this.onClickSignUp}>Sign Up</button>
                </div>
            </form>
        );
    }


    // We dont want to repeat our code 
    // onChangeUsername = event => {
    //     this.setState({
    //         username: event.target.value
    //     });
    // };

    // onChangeDisplayName = event => {
    //     this.setState({
    //         displayname: event.target.value
    //     });
    // };
    
    // onChangePassword = event => {
    //     this.setState({
    //         password: event.target.value
    //     });
    // };

    // onChangePasswordRepeat = event => {
    //     this.setState({
    //         passwordRepeat: event.target.value
    //     });
    // };

}

export default UserSignUpPage;
